import Notification from './notification.vue'

export default {
  extends: Notification,
  computed: {
    style () {
      return {
        position: 'fixed',
        right: '20px',
        bottom: `${this.verticalOffset}px` // 在实例化此组件时添加了属性
      }
    }
  },
  data () {
    return {
      verticalOffset: 0, // 声明属性
      autoClose: 0, // 声明属性
      visible: false, // 声明属性，注意这里，我们的模板notification.vue的visible是true，我们继承的此组件类如果也为true则不会触发transition
      height: 0 // 在transition动画结束后取到值
    }
  },
  mounted () {
    this.createTimer()
  },
  methods: {
    createTimer () {
      if (this.autoClose) {
        this.timer = setTimeout(() => {
          this.visible = false
        }, this.autoClose);
      }
    },
    clearTimer () {
      if (this.timer) {
        clearTimeout(this.timer)
      }
    },
    afterEnter () { // 覆盖父类此方法
      // debugger // eslint-disable-line
      this.height = this.$el.offsetHeight
    }
  },
  beforeDestroy () {
    this.clearTimer()
  }
}

import Component from './func-notification'
import Vue from 'vue'

const NotificationConstructor = Vue.extend(Component)

const instances = [] // 保存已创建的instance列表
let seed = 1 // 生成组件id
const notify = (options) => {
  if (Vue.prototype.$isServer) return

  const {autoClose, ...rest} = options
  const instance = new NotificationConstructor({
    propsData: {
      ...rest
    },
    data () {
      return {
        autoClose: autoClose === undefined ? 3000 : autoClose
      }
    }
  })
  const id = `notification_${seed++}`
  instance.id = id
  instance.vm = instance.$mount() // 生成el
  document.body.appendChild(instance.vm.$el) // 全局组件放在body下面

  let verticalOffset = 0
  instances.forEach(item => {
    verticalOffset += item.$el.offsetHeight + 16
  })
  verticalOffset += 16
  instance.verticalOffset = verticalOffset
  instance.vm.visible = true
  instances.push(instance)

  const removeInstance = (instance) => {
    if (!instance) return
    const index = instances.findIndex(inst => instance.id === inst.id)
    if (index) {
      instances.splice(index, 1)
    }
    // 删除节点后重新计算它上面的节点高度
    let length = instances.length
    if (length < 1) return
    const removeHeight = instance.vm.height
    for (let i = index; i <= length - 1; i++) {
      instances[i].verticalOffset = instances[i].verticalOffset - removeHeight - 16
    }
  }
  instance.vm.$on('close', () => { // 删除节点
    removeInstance(instance)
    document.body.removeChild(instance.vm.$el) // 删除真实dom节点
    instance.vm.$destroy() // 然后再删除对应dom节点的vm对象
  })

  return instance.vm
}

export default notify

